def fourSumCount(A, B, C, D):
	first_sum = {}
	second_sum = {}
	sol = []
	sol_count = 0
	for i in range(0, len(A)):
		for j in range(0, len(B)):
			addition = A[i] + B[j]
			indices = [i,j]
			if addition in first_sum.keys():
				first_sum[addition].append(indices)
			else:
				first_sum[addition] = [indices]
	
	for i in range(0, len(C)):
		for j in range(0, len(D)):
			addition = C[i] + D[j]
			indices = [i,j]
			if addition in second_sum.keys():
				second_sum[addition].append(indices)
			else:
				second_sum[addition] = [indices]
	
	for first_num in first_sum.keys():
		if -first_num in second_sum.keys():
			sol_count += len(first_sum[first_num]) * len(second_sum[-first_num])
